import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';

export const Btn = withStyles({
  root: {
    boxShadow: 'none',
    textTransform: 'none',
    fontSize: 16,
    padding: '6px 12px',
    border: '1px solid',
    lineHeight: 1.5,
    backgroundColor: '#5C0090',
    borderColor: '#5C0090',
    '&:hover': {
      backgroundColor: '#2D283E',
      borderColor: '#2D283E',
      boxShadow: 'none',
    },
    '&:active': {
      boxShadow: 'none',
    },
    '&:focus': {
      boxShadow: '0 0 0 0.2rem rgba(0,123,255,.5)',
    },
  },
})(Button);
