import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import MenuIcon from '@material-ui/icons/Menu';
import PropTypes from 'prop-types';
import Btn from '../Button/Btn';
import clsx from 'clsx'
import {Link} from 'react-router-dom'

const useStyles = makeStyles((theme) => ({
navsolid : {
    background : "#fff",
    color : "#5C0090",
},
nav : {
    background: (props) =>
    props.color === 'transparent'
      ? 'transparent'
      : '#fff',
      color: (props) => 
      props.color === 'transparent'
      ? '#ffffff'
      : '#5C0090',
    justifyContent : "center",
    boxShadow: '0 3px 5px 2px rgba(0, 0, 0, .1)',
    height: 80,
    width : "100%"
    },
toolbar : {
    marginLeft : "10%",
    marginRight : "10%",
    [theme.breakpoints.down('sm')]: {
        marginLeft : "0%",
        marginRight : "0%"
      },
},
  grow: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    display: 'none',
    [theme.breakpoints.up('md')]: {
      display: 'block',
    },
  },
  sectionDesktop: {
    display: 'none',
    [theme.breakpoints.up('md')]: {
      display: 'flex',
    },
  },
  sectionMobile: {
    display: 'flex',
    [theme.breakpoints.up('md')]: {
      display: 'none',
    },
  },
  link : { 
    textDecoration : "none",
    color: "inherit",
    [theme.breakpoints.down('sm')]: {
      color : "inherit"
    },
  },
  popoverPaper: {
    width: '100%',
    height: '100%',
    maxHeight: 'unset',
    maxWidth: 'unset',
  }
}));

  Navbar.propTypes = {
    color: PropTypes.oneOf(['transparent', 'white']).isRequired,
  };

export default function Navbar(props) {
  const { color, ...other } = props;
  const navclasses = useStyles(props);

  const classes = useStyles();
  const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = React.useState(null);
  const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);

  const handleMobileMenuClose = () => {
    setMobileMoreAnchorEl(null);
  };

  const handleMobileMenuOpen = (event) => {
    setMobileMoreAnchorEl(event.currentTarget);
  };

  const [nav , setNavbar] = React.useState('navsolid');
  const navRef = React.useRef();
  navRef.current = nav
  const changeBackground = () => {
    if(window.scrollY > 100){
        setNavbar('nav')
    }else{
        setNavbar('navsolid')
    }
}

  window.addEventListener('scroll', changeBackground);

  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const mobileMenuId = 'primary-search-account-menu-mobile';
  const renderMobileMenu = (
    <Menu
      anchorEl={mobileMoreAnchorEl}
      anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
      id={mobileMenuId}
      keepMounted
      transformOrigin={{ vertical: 'top', horizontal: 'right' }}
      open={isMobileMenuOpen}
      onClose={handleMobileMenuClose}
    >
    <MenuItem>
    <Link className = {classes.link} to = "/catalog">Courses</Link>
    </MenuItem>
    <MenuItem>
    <Link className = {classes.link} to = "/">Tracks</Link>
    </MenuItem>
    <MenuItem>
    <Link className = {classes.link} to = "/">Blog</Link>
    </MenuItem>
    <MenuItem>
    <Link className = {classes.link} to = "/login">Login</Link>
    </MenuItem>
    <MenuItem>
    <Link className = {classes.link} to = "/signup">Signup</Link>
    </MenuItem>
    </Menu>
  );

  return (
    <div className={classes.grow}>
      <AppBar position="fixed" className={clsx(navclasses.nav ,classes[navRef.current])} {...other}>
        <Toolbar className = {classes.toolbar}>
          <Link className = {classes.link} to = "/">
          <Typography variant="h1" noWrap>
            COURSENATOR
          </Typography>
          </Link>
          <div className={classes.grow} />
          <div className={classes.sectionDesktop}>
              <MenuItem>
              <Link className = {classes.link} aria-controls="mega-menu" aria-haspopup="true" onClick={handleClick}>
                Courses
                </Link>
              </MenuItem>
              <Menu
                id="mega-menu"
                anchorEl={anchorEl}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose}
                PopoverClasses={{paper: props.classes.popoverPaper}}
              >
                <MenuItem onClick={handleClose}>Data Science</MenuItem>
                <MenuItem onClick={handleClose}>Business</MenuItem>
                <MenuItem onClick={handleClose}>Humanities</MenuItem>
                <MenuItem onClick={handleClose}>Art and Design</MenuItem>
                <MenuItem onClick={handleClose}>Programming</MenuItem>
                <MenuItem onClick={handleClose}>Personal Developement</MenuItem>
                <MenuItem onClick={handleClose}>Health and Nutrition</MenuItem>
                <MenuItem onClick={handleClose}>Language Learning</MenuItem>
                <MenuItem onClick={handleClose}>Computer Science</MenuItem>
                <MenuItem onClick={handleClose}>Information Technology</MenuItem>
                <MenuItem onClick={handleClose}>Social Science</MenuItem>
                <MenuItem onClick={handleClose}>Physical Science and Education</MenuItem>
                <MenuItem onClick={handleClose}>Education and Teaching</MenuItem>
              </Menu>
              <MenuItem>
              <Link className = {classes.link} to = "/">Tracks</Link>
              </MenuItem>
              <MenuItem>
              <Link className = {classes.link} to = "/">Blogs</Link>
              </MenuItem>
              <MenuItem>
              <Link className = {classes.link} to = "/login">Login</Link>
              </MenuItem>
              <Btn color = "white">
              <Link className = {classes.link} to = "/signup">Signup</Link>
              </Btn>
          </div>
          <div className={classes.sectionMobile}>
            <IconButton
              aria-label="show more"
              aria-controls={mobileMenuId}
              aria-haspopup="true"
              onClick={handleMobileMenuOpen}
              color="inherit"
            >
              <MenuIcon />
            </IconButton>
          </div>
        </Toolbar>
      </AppBar>
      {renderMobileMenu}
    </div>
  );
}
