import { createMuiTheme } from '@material-ui/core'

//Theme for Coursenator

export const theme = createMuiTheme({
    palette: {
        primary :{
            main : "#5C0090"
        },
        secondary :{
            main : "#2D283E"
        }
    },
    typography : {
        fontFamily : "Nunito",
        fontSize : "14"
    },
    h1:{
        fontSize : "20",
        fontWeight : "600"
    },
    caption : {
        fontsize : "24px",
        fontFamily : "Skranji",
        fontWeight : "600"
    }
})
