import styled from 'styled-components';
import { Button } from '../../globalStyles';

export const AboutSec = styled.div`
  color: var(--white);
  padding: 160px 0;
  background: var(--white);
`;

export const AboutRow = styled.div`
  display: flex;
  margin: 0 -15px -15px -15px;
  flex-wrap: wrap;
  align-items: center;
  flex-direction: ${({ imgStart }) => (imgStart ? 'row-reverse' : 'row')};
`;

export const AboutColumn = styled.div`
  margin-bottom: 15px;
  padding-right: 15px;
  padding-left: 15px;
  flex: 1;
  max-width: 50%;
  flex-basis: 50%;
  @media screen and (max-width: 768px) {
    max-width: 100%;
    flex-basis: 100%;
    display: flex;
    justify-content: center;
  }
`;

export const TextWrapper = styled.div`
  max-width: 500  px;
  padding-top: 0;
  padding-left : 15%;
  @media screen and (max-width : 768px){
    padding-left : 0;
  }
`;

export const ImgWrapper = styled.div`
  max-width: 700px;
  display: flex;
  justify-content: ${({ start }) => (start ? 'flex-start' : 'flex-end')};
`;

export const Img = styled.img`
  padding-right: 0;
  border: 0;
  max-width: 100%;
  vertical-align: middle;
  display: inline-block;
  max-height: 500px;
  @media screen and (max-width : 768px){
    padding : 25px;
    max-height: 450px;
    max-width : 500px;
  }
`;

export const Text = styled.p`
  text-align:center;
  max-width: 440px;
  margin-bottom: 25px;
  font-size: 18px;
  line-height: 24px;
  color: ${({ black }) => (black ? '#000; ' : 'var(--grey)') };
  text-shadow: ${({ dropshadow }) => (dropshadow ? '5px 4px 4px rgba(0, 0, 0, 0.20); ' : 'none') };
  font-weight: ${({ bold }) => (bold ? 'bold; ' : 'none') };
`;

export const AboutSecButton = styled(Button)`
  padding: ${({ bigger }) => (bigger ? '10px 55px' : '10px 30px')};
  margin-left: 25px;
  @media screen and (max-width: 960px){
    margin-top: 10px;
    margin-left: 10%;
    width: 80%;
  }

`
